import React, {Component} from 'react';

const data = [
    {img: '/images/perpusci.png', title: 'PerpusCi', technology: ['Codeigniter', 'Bootstrap']},
    {img: '/images/perpusci.png', title: 'BuraQApp', technology: ['Springboot', 'React js', 'Redux']},
]

class Portfolio extends Component {
    render() {
        const dataLoop = data.map((value) => {
            return (
                <div className="pb-2">
                    <div className="card mb-3">
                        <img src={value.img} alt={value.title} className="card-img-top h-100"/>
                        <div className="card-text">
                            <h3 className="pb-5">{value.title}</h3>
                            <h6 className="card-text">{value.technology.join(', ')}</h6>
                        </div>
                    </div>
                </div>
            )
        })

        return (
            <section id="portfolio" className="container pb-5">
                <div className="row">
                    <div className="col">
                        <div className="h2 text-uppercase pt-4 mb-3 pb-3">Portfolio</div>
                        {dataLoop}
                    </div>
                </div>
            </section>
        );
    }
}

export default Portfolio;
