import React, {Component} from 'react'
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faDownload} from '@fortawesome/free-solid-svg-icons'

class Home extends Component {
    render() {
        let today = new Date();
        let birthDate = new Date("2001-11-17")
        let year = 0;
        if (today.getMonth() < birthDate.getMonth()) {
            year = 1;
        } else if (today.getMonth() === today.getMonth() && today.getDate() < birthDate.getDate()) {
            year = 1;
        }

        let age = today.getFullYear() - birthDate.getFullYear() - year;
        return (
            <>
                <div className="container d-flex justify-content-center align-items-center min-vh-100">
                    <div className="row">
                        <div className="col-md-6 pt-6 order-1 order-md-0">
                            <h1 className="h5">Hi, I`m Waisal Riyanto Saputra</h1>
                            <h1 className="h3">Junior Web Programmer</h1>
                            <p className="text-justify">I'm age {age} years old and I'm graduated on software
                                engineering. after graduating, I follow of the training in <strong>BBPLK
                                    BEKASI</strong> on web development and at <strong>ENIGMA BOOTCAMP</strong> on
                                full-stack software engineering.<br/> there I learn technology like PHP, JAVA,
                                JAVASCRIPT,
                                REACTJS, REACT NATIVE, SPRING BOOT, NODE JS AND EXPRESS JS.
                                and currently, I focused learn fundamentals java and javascript
                            </p>
                            <a href="mailto:waisal.riyanto@gmail.com" className="btn btn-sm btn-dark text-light"
                               aria-label={"Left Align"}>
                                <FontAwesomeIcon icon={faDownload} aria-hidden={true}/> &nbsp;
                                Download CV
                            </a>
                        </div>
                        <div className="col-md-6">
                            <img src="/images/me2.png" className="img-fluid" alt=""/>
                        </div>
                    </div>
                </div>
            </>
        );
    }
}

export default Home;
