import React, {Component} from 'react';
import {faBook} from "@fortawesome/free-solid-svg-icons/faBook";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

const data = [
    {img: '/images/perpusci.png', title: 'PerpusCi', technology: ['Codeigniter', 'Bootstrap']},
    {img: '/images/perpusci.png', title: 'BuraQApp', technology: ['Springboot', 'React js', 'Redux']},
]

class Portfolio extends Component {

    render() {
        const dataLoop = data.map((value) => {
            return (
                <div className="card mb-4">
                    <img src={value.img} alt={value.title} className="card-img-top h-100"/>
                    <div className="card-text">
                        <h3>{value.title}</h3>
                        <h6>{value.technology.join(', ')}</h6>
                        <a href="" className="btn btn-light btn-sm">
                            <FontAwesomeIcon icon={faBook}/> &nbsp;
                            source code</a>
                    </div>
                </div>
            )
        })

        return (
            <div className="container pb-5">
                <div className="row">
                    <div className="col-12">
                        <div className="h2 project-title pt-4 mb-3 pb-3">My Projects</div>
                        {dataLoop}
                    </div>
                </div>
            </div>
        );
    }
}

export default Portfolio;
