import React, {Component} from 'react'
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faDownload} from '@fortawesome/free-solid-svg-icons'
import axios from 'axios'
import FormData from 'form-data'

class Home extends Component {
    constructor(props) {
        super(props);
    }

    doDownload() {
        let myHeaders = new Headers();
        myHeaders.append("Authorization", "Bearer pqH_qLW_DaAAAAAAAAAAWyzBgTSmXtISnK0-BjvmgbjEKbixcSoHhUs0q5-lmxpd");
        myHeaders.append("Dropbox-API-Arg", "{\"path\": \"/cv.docx\"}");

        let formdata = new FormData();

        let requestOptions = {
            method: 'POST',
            headers: myHeaders,
            body: formdata,
            redirect: 'follow'
        };

        fetch("https://content.dropboxapi.com/2/files/get_preview", requestOptions)
            .then(response => response.text())
            .then(result => result)
            .catch(error => console.log('error', error));
    }

    render() {
        let today = new Date();
        let birthDate = new Date("2001-11-17")
        let year = 0;
        if (today.getMonth() < birthDate.getMonth()) {
            year = 1;
        } else if (today.getMonth() === today.getMonth() && today.getDate() < birthDate.getDate()) {
            year = 1;
        }

        let age = today.getFullYear() - birthDate.getFullYear() - year;
        return (
            <>
                <div className="container d-flex justify-content-center align-items-center min-vh-100">
                    <div className="row">
                        <div className="col-md-6 pt-6 order-1 order-md-0">
                            <div className="h5">Hi,</div>
                            <div className="h5">I`m Waisal Riyanto Saputra

                            </div>
                            <div className="h3">Junior Web Programmer</div>
                            <p className="text-justify">I'm {age} years old, I've been learning programming from
                                vocational high
                                school. after graduate, I follow of the training at <strong>BBPLK
                                    BEKASI</strong> as a web developer in 2 months and at <strong>ENIGMA
                                    BOOTCAMP</strong> as a
                                full-stack software engineer in 3 months.<br/> there, I learned new things such as PHP,
                                JAVA,
                                JAVASCRIPT,
                                REACTJS, REACT NATIVE, SPRING BOOT, MYSQL, NODE JS, AND EXPRESS JS.<br/>
                                and currently, I focused learn fundamentals java, javascript, and version control git
                                for
                                maximizing when learning framework.
                            </p>
                            <button onClick={() => this.doDownload()} className="btn btn-sm btn-dark text-light"
                                    aria-label={"Left Align"}>
                                <FontAwesomeIcon icon={faDownload} aria-hidden={true}/> &nbsp;
                                Download CV
                            </button>
                        </div>
                        <div className="col-md-6">
                            <img src="/images/me2.png" className="img-fluid" alt=""/>
                        </div>
                    </div>
                </div>
            </>
        );
    }
}

export default Home;
